function xml2json(b){var a={};if(1==b.nodeType){if(0<b.attributes.length){a["@attributes"]={};for(var c=0;c<b.attributes.length;c++){var e=b.attributes.item(c);a["@attributes"][e.nodeName]=e.nodeValue}}}else if(3==b.nodeType)return b.nodeValue;if(b.hasChildNodes())for(c=0;c<b.childNodes.length;c++){var e=b.childNodes.item(c),d=e.nodeName;if(null==a[d])a[d]=xml2json(e);else{if(null==a[d].push){var f=a[d];a[d]=[];a[d].push(f)}a[d].push(xml2json(e))}}return a};
